    private void jButton12ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton12ActionPerformed
//.        
             String cadena;
             cadena=TXTpantalla.getText();
             if (cadena.length()<=0) {
             TXTpantalla.setText("0.");
             }
             else {
             if (!existepunto(TXTpantalla.getText())) {
                 TXTpantalla.setText(TXTpantalla.getText()+".");
                       }
           
            
    }//GEN-LAST:event_jButton12ActionPerformed
    }   
public static boolean existepunto (String cadena){
       boolean resultado;
       resultado=false;
       for (int i=0; i<cadena.length();i++) { //mientras hay valor sigue buscando, no hay nada, pone punto
          if (cadena.substring(i, i+1).equals(".")) { //equals significa colocar
           resultado=true;//como ya no es falso termina
           break;//termina el for
       }
       }
       return resultado;
   }
    private void jButton17ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton17ActionPerformed
//=       
           
             String resultado;
             String memoria1 = TXTpantalla.getText();
        String memoria2 = TXTpantalla.getText();
        if (!memoria2.equals(" ")){
                 
           resultado=calculadora(memoria1,memoria2,signo); 
           TXTpantalla.setText(resultado);
        }
    }//GEN-LAST:event_jButton17ActionPerformed
    public static String calculadora(String memoria1,String memoria2, String signo) {
           Double resultado=0.0;
           String respuesta;
           if (signo.equals("-")){
               resultado=Double.parseDouble(memoria1)-Double.parseDouble(memoria2);
           }
           if (signo.equals("+")){
               resultado=Double.parseDouble(memoria1)+Double.parseDouble(memoria2);
           } 
             if (signo.equals("*")){
               resultado=Double.parseDouble(memoria1)*Double.parseDouble(memoria2);
           }
               if (signo.equals("/")){
               resultado=Double.parseDouble(memoria1)/Double.parseDouble(memoria2);
           }
               respuesta=resultado.toString();
               return respuesta;
}
